sys_script.result:
(function executeRule(current, previous /*null when async*/) {
    if (current.u_officer_decision == 'approved' || current.u_officer_decision == 'rejected') {
        current.u_decision_date = new GlideDateTime();
        current.u_application_status = current.u_officer_decision;
    }
})(current, previous);

